mapper.select:
select stock_code from stock
where stock_code not in (
    select distinct t1.stock_code from avg_line as t1, avg_line as t2
    where t1.date >= #{start} and t1.date <= #{end}
    and t1.stock_code = t2.stock_code
    and t1.date = t2.date
    and t1.statistic = #{highStats}
    and t2.statistic = #{lowStats}
    and t1.avg < t2.avg
)
order by stock_code;

select stock_code from stock as s
where not exists (
    select * from v_avg_line
    where stock_code = s.stock_code
    and date >= #{start} and date <= #{end}
    and avg5 < avg10
)
order by stock_code;

select * from v_avg_line


        and date >= #{start}


        and date <= #{end}


        and stock_code in

            #{code}




    limit #{limit}


    offset #{offset}

mapper.insert:
insert into avg_line_5 (stock_code, date, current, avg) values
(#{item.stockCode}, #{item.date}, #{item.current}, #{item.avg5})
on conflict (stock_code, date) do update set current = excluded.current, avg = excluded.avg;
insert into avg_line_10 (stock_code, date, current, avg) values
(#{item.stockCode}, #{item.date}, #{item.current}, #{item.avg10})
on conflict (stock_code, date) do update set current = excluded.current, avg = excluded.avg;
insert into avg_line_20 (stock_code, date, current, avg) values
(#{item.stockCode}, #{item.date}, #{item.current}, #{item.avg20})
on conflict (stock_code, date) do update set current = excluded.current, avg = excluded.avg;
insert into avg_line_30 (stock_code, date, current, avg) values
(#{item.stockCode}, #{item.date}, #{item.current}, #{item.avg30})
on conflict (stock_code, date) do update set current = excluded.current, avg = excluded.avg;
